import React, { useEffect } from "react";
import { ChainLink, EvolutionChain } from "../features/evolutionChainSlice";
import { Pokemon } from "../features/pokemonSlice";
import { Species } from "../features/speciesSlice";
import { NamedAPIResource } from "../features/types";

import PokemonEvolution from "./PokemonEvolution";

type Props = {
  pokemon: Pokemon;
  species: Species;
  evolutionChain: EvolutionChain | null;
  selectedBackgroundColor: { light: string; medium: string };
};

const PokemonDetailsEvolutions = ({
  pokemon,
  species,
  evolutionChain,
  selectedBackgroundColor,
}: Props) => {
  const getAllPokemonEvolutions = () => {
    if (evolutionChain?.chain) {
      const pokemons: NamedAPIResource[] = getPokemonEvolution(
        evolutionChain?.chain
      );

      console.log(pokemons);
    }
  };
  const getPokemonEvolution = (chain: ChainLink | null): NamedAPIResource[] => {
    // base terminating condition
    if (chain) {
      return [chain.species].concat(getPokemonEvolution(chain.evolvesTo[0]));
    } else {
      return [];
    }
  };

  console.log(getAllPokemonEvolutions());

  return (
    <div className="mt-12 text-center ">
      {evolutionChain === null ? (
        <h2>No Evolution Chain</h2>
      ) : (
        <div className="lg:grid lg:grid-cols-2 lg:gap-y-10">
          <PokemonEvolution
            pokemon={pokemon}
            selectedBackgroundColor={selectedBackgroundColor}
          />

          <PokemonEvolution
            pokemon={pokemon}
            selectedBackgroundColor={selectedBackgroundColor}
          />
          <PokemonEvolution
            pokemon={pokemon}
            selectedBackgroundColor={selectedBackgroundColor}
          />
        </div>
      )}
    </div>
  );
};

export default PokemonDetailsEvolutions;
