import React, { useCallback, useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { ChainLink, EvolutionChain } from "../features/evolutionChainSlice";
import {
  getPokemonById,
  Pokemon,
  pokemonsSelector,
} from "../features/pokemonSlice";
import { Species } from "../features/speciesSlice";
import { NamedAPIResource } from "../features/types";

import PokemonEvolution from "./PokemonEvolution";

type Props = {
  selectedIds: number[];
  chainLinks: ChainLink[];
  selectedBackgroundColor: { light: string; medium: string };
};

const PokemonDetailsEvolutions = ({
  selectedBackgroundColor,
  selectedIds,
  chainLinks,
}: Props) => {
  const pokemons = useSelector(pokemonsSelector);

  const pokemonEvolutions: (Pokemon | null)[] = pokemons.data.filter(
    (pokemon) => pokemon !== null && selectedIds.includes(pokemon.id)
  );

  return (
    <div className="mt-12 text-center ">
      {selectedIds === null ? (
        <h2>No Evolution Chain</h2>
      ) : (
        <div className="lg:grid lg:grid-cols-2 lg:gap-y-10">
          {pokemonEvolutions.map((pokemon) => (
            <>
              {pokemon && (
                <PokemonEvolution
                  pokemon={pokemon}
                  chain={chainLinks.find(
                    ({ species }) =>
                      Number(species.url.split("/").splice(-2)[0]) ===
                      pokemon.id
                  )}
                  selectedBackgroundColor={selectedBackgroundColor}
                />
              )}
            </>
          ))}
        </div>
      )}
    </div>
  );
};

export default PokemonDetailsEvolutions;
