import React from "react";

type Props = {
  title: string;
  min: number;
  max: number;
};

const PokemonStats = ({ title, min, max }: Props) => {
  return (
    <div className="grid grid-cols-5">
      <span>{title}</span>
      <span>{min}</span>
      <div className="col-span-2 w-full shadow bg-grey-light">
        <div
          className="bg-primary text-xs leading-none py-1 text-center text-white"
          style={{
            width: "65%",
          }}
        />
      </div>
      <span>{max}</span>
    </div>
  );
};
export default PokemonStats;
