import React from "react";

type Props = {
  title: string;
  min: number;
  max: number;
};

const PokemonStats = ({ title, min, max }: Props) => {
  return (
    <div className="grid grid-cols-5">
      <span>{title}</span>
      <span>{min}</span>
      <div className="col-span-2 w-full">
        <div className="shadow w-full bg-grey-light mt-2 rounded-lg">
          <div
            className="bg-primary py-1"
            style={{
              width: "65%",
            }}
          />
        </div>
      </div>
      <span>{max}</span>
    </div>
  );
};
export default PokemonStats;
