import React from "react";

type Props = {
  title: string;
  min: number;
  max: number;
};

const PokemonStats = ({ title, min, max }: Props) => {
  return (
    <div className="grid grid-cols-5 gap-2 md:gap-4 lg:gap-8 mt-2 md:mt-3">
      <span className="opacity-75">{title}</span>
      <span className="text-center">{min}</span>
      <div className="col-span-2 w-full align-middle">
        <div className="shadow w-full bg-grey-light mt-2 rounded-lg">
          <div
            className="bg-primary py-1 rounded-lg"
            style={{
              width: `${(min / max) * 100}%`,
            }}
          />
        </div>
      </div>
      <span className="text-center">{max}</span>
    </div>
  );
};
export default PokemonStats;
