import React from "react";
import { AiOutlineCaretDown } from "react-icons/ai";
import { Pokemon } from "../features/pokemonSlice";

import PokemonEvolution from "./PokemonEvolution";

type Props = {
  pokemon: Pokemon;
  selectedBackgroundColor: { light: string; medium: string };
};

const PokemonDetailsEvolutions = ({
  pokemon,
  selectedBackgroundColor,
}: Props) => {
  return (
    <div className="mt-6 text-center lg:grid lg:grid-cols-3">
      <div>
        <PokemonEvolution
          pokemon={pokemon}
          selectedBackgroundColor={selectedBackgroundColor}
        />
        <p className="flex items-center mx-auto">
          <AiOutlineCaretDown
            className="block mx-auto lg:appearance-none"
            size={16}
          />
        </p>
      </div>
      <div>
        <PokemonEvolution
          pokemon={pokemon}
          selectedBackgroundColor={selectedBackgroundColor}
        />
        <p className="flex items-center mx-auto">
          <AiOutlineCaretDown className="block mx-auto" size={16} />
        </p>
      </div>
      <div>
        <PokemonEvolution
          pokemon={pokemon}
          selectedBackgroundColor={selectedBackgroundColor}
        />
        <p className="flex items-center mx-auto">
          <AiOutlineCaretDown className="block mx-auto" size={16} />
        </p>
      </div>
    </div>
  );
};

export default PokemonDetailsEvolutions;
