import React, {
  useState,
  useContext,
  createContext,
  useEffect,
  useCallback,
} from "react";
import { PAGINATE_SIZE } from "../features/pokemonSlice";
import LoadButton from "./LoadButton";
import { Waypoint as ReactWaypoint } from "react-waypoint";
import { NamedAPIResource } from "../features/types";

type ContextType = {
  page: number;
  setPage: React.Dispatch<React.SetStateAction<number>>;
  isLoading: boolean;
  isFetching: boolean;
  paginationHandler: (page: number) => Promise<any[]>;
  paginationUpdater: (
    index: number,
    size: number,
    data: NamedAPIResource
  ) => Promise<void>;
};
const InfiniteScrollContext = createContext<ContextType>({
  page: 0,
  setPage: () => {},
  isLoading: true,
  isFetching: false,
  paginationHandler: async () => [],
  paginationUpdater: async () => {},
});

const Waypoint = () => {
  const {
    setPage,
    page,
    paginationHandler,
    paginationUpdater,
    isFetching,
  } = useContext(InfiniteScrollContext);
  const [index, setIndex] = useState<number>(0);
  const [fetchData, setFetchData] = useState<any[]>([]);

  const initializeNewPage = useCallback(async () => {
    if (!isFetching) {
      setPage(page + PAGINATE_SIZE);
      const data = await paginationHandler(page);
      const startingIndex = [...data].reverse().findIndex((d) => d !== null);
      const determinedIndex = startingIndex === -1 ? 0 : startingIndex;
      setIndex(determinedIndex);
      setFetchData(data);
    }
  }, [isFetching, page, paginationHandler, setPage]);

  useEffect(() => {
    if (isFetching && index < PAGINATE_SIZE && fetchData[index]) {
      setTimeout(() => {
        paginationUpdater(index, PAGINATE_SIZE, fetchData[index]).then(() =>
          setIndex(index + 1)
        );
      }, 1000);
    }
  }, [index, isFetching, fetchData, paginationUpdater]);

  return (
    <div className="mt-48">
      <ReactWaypoint onEnter={async () => initializeNewPage()} />
    </div>
  );
};

const Button = () => {
  const { isLoading, setPage, page } = useContext(InfiniteScrollContext);
  return (
    <div className="py-16 mx-auto">
      {isLoading ? null : (
        <div className="mt-6">
          <LoadButton
            clickHandler={() => {
              setPage(page + PAGINATE_SIZE);
            }}
          />
        </div>
      )}
    </div>
  );
};

type ContainerProps = {
  children: React.ReactNode;
};
const Container = ({ children }: ContainerProps) => {
  return (
    <div className="w-full mx-auto grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-x-8 lg:gap-x-5 gap-y-6">
      {children}
    </div>
  );
};

type InfiniteScrollProps = {
  children: ({
    mutatePage: resetPage,
  }: {
    mutatePage: React.Dispatch<React.SetStateAction<number>>;
  }) => React.ReactNode;
  paginationHandler: (page: number) => Promise<any[]>;
  paginationUpdater: (
    index: number,
    size: number,
    data: NamedAPIResource
  ) => Promise<void>;
  isLoading: boolean;
  isFetching: boolean;
};

const InfiniteScroll = ({
  children,
  paginationHandler,
  paginationUpdater,
  isLoading,
  isFetching,
}: InfiniteScrollProps) => {
  const [page, setPage] = useState(0);

  return (
    <InfiniteScrollContext.Provider
      value={{
        page,
        setPage,
        isLoading,
        isFetching,
        paginationHandler,
        paginationUpdater,
      }}
    >
      {children({ mutatePage: setPage })}
    </InfiniteScrollContext.Provider>
  );
};

InfiniteScroll.Container = Container;
InfiniteScroll.Button = Button;
InfiniteScroll.Waypoint = Waypoint;
export default InfiniteScroll;
