import React, { useCallback, useEffect, useState } from "react";
import PokemonForm from "../components/PokemonForm";

import Layout from "../components/Layout";
import InfiniteScroll from "../components/InfiniteScroll";
import PokemonCard from "../components/PokemonCard";
import { useDispatch, useSelector } from "react-redux";
import {
  pokemonsSelector,
  getPokemonsReducer,
  PAGINATE_SIZE,
  initializePokemonsReducer,
  getPokemons,
} from "../features/pokemonSlice";
import { SliceStatus } from "../globals";
import { cachedPokemonsSelector } from "../features/cachedPokemonsSlice";
import PokemonSkeleton from "../components/PokemonSkeleton";
import fromApi from "../api/fromApi";
import { transformSpriteToBaseImage } from "../features/utilities";
import { baseImageUrl } from "../api/axios";
import { camelcaseObject } from "../utils/camelcaseObject";
import { NamedAPIResource } from "../features/types";

const PokemonsPage = () => {
  const pokemons = useSelector(pokemonsSelector);
  const cachedPokemons = useSelector(cachedPokemonsSelector);
  const dispatch = useDispatch();

  const fetchPokemons = useCallback(
    async (page: number) => {
      const size = PAGINATE_SIZE - (pokemons.data.length % PAGINATE_SIZE);
      dispatch(initializePokemonsReducer({ size }));
      return cachedPokemons.data.slice(page, page + size);
    },
    [cachedPokemons.data, pokemons.data, dispatch]
  );

  const [isFetching, setIsFetching] = useState<boolean>(false);

  useEffect(() => {
    setIsFetching(
      pokemons.data
        .slice(Math.max(pokemons.data.length - PAGINATE_SIZE, 1))
        .findIndex((pokemon) => pokemon === null) === -1
        ? false
        : true
    );
  }, [pokemons.data, isFetching]);

  return (
    <Layout title="Home">
      <h1 className="text-3xl lg:text-5xl font-semibold text-center sm:text-left">
        React Pokédex
      </h1>

      <InfiniteScroll
        isLoading={pokemons.status.state === SliceStatus.LOADING}
        isFetching={isFetching}
        paginationHandler={(page: number) => fetchPokemons(page)}
        paginationUpdater={async (
          index: number,
          size: number,
          data: NamedAPIResource
        ) => {
          dispatch(getPokemons({ index, size, data }));
        }}
      >
        {({ mutatePage }) => (
          <>
            <div className="my-4 md:my-6 lg:my-8 w-full">
              <PokemonForm
                placeholder="Search for a pokémon..."
                mutatePage={mutatePage}
              />
            </div>
            <div className="mx-auto w-full text-center">
              {!(
                cachedPokemons.status.state === SliceStatus.LOADING ||
                cachedPokemons.status.state === SliceStatus.IDLE
              ) && (
                <>
                  <InfiniteScroll.Container>
                    {pokemons.data.map((pokemon, index) =>
                      pokemon === null ? (
                        <div key={`loading-${index}`}>
                          <PokemonSkeleton />
                        </div>
                      ) : (
                        <PokemonCard key={pokemon.id} {...pokemon} />
                      )
                    )}
                  </InfiniteScroll.Container>
                  <InfiniteScroll.Waypoint />
                </>
              )}
            </div>
          </>
        )}
      </InfiniteScroll>
    </Layout>
  );
};
export default PokemonsPage;
