import React from "react";
import ProgressiveImage from "react-progressive-image-loading";
import { Pokemon } from "../features/pokemonSlice";
import { PokemonTypePlaceholders } from "../globals";
import { leftPad } from "../utils/leftPad";

const MaskStyling = {
  width: 130,
  height: 130,
  zIndex: -10,
  bottom: 8,
  left: 16,
};
const ImageContainerStyling = {
  width: 175,
  height: 175,
};

type Props = {
  pokemon: Pokemon;
  selectedBackgroundColor: { light: string; medium: string };
};

const PokemonEvolution = ({ pokemon, selectedBackgroundColor }: Props) => {
  const imagePlaceholder = pokemon.types.map(({ type }) => {
    const [[, image]] = Object.entries(PokemonTypePlaceholders).filter(
      ([key, _]) => key === type.name
    );

    return image;
  });

  return (
    <div className="">
      <div
        className="py-32 mx-auto w-full flex items-center justify-center relative"
        style={{
          backgroundColor: selectedBackgroundColor.medium,
        }}
      >
        <p className="text-6xl font-semibold text-black text-opacity-25 absolute tracking-xl top-1/8 pointer-events-none">
          #{leftPad(pokemon.id, 3)}
        </p>

        <div
          className="inset-x-auto bottom-0 absolute z-20"
          style={ImageContainerStyling}
        >
          <div
            className="rounded-full absolute z-0 inset-x-auto mx-auto"
            style={{
              ...MaskStyling,
              backgroundColor: selectedBackgroundColor.light,
            }}
          />
          <ProgressiveImage
            preview={imagePlaceholder[0]}
            src={pokemon.sprites.frontDefault}
            render={(src, style) => (
              <img src={src} style={style} alt={pokemon.name} />
            )}
          />
        </div>
      </div>
    </div>
  );
};

export default PokemonEvolution;
